import "./Description.css";

const Descripation = () => {
  return (
    <div className="description">
      <div className="description-box-navigator">
        <div className="description-box-nav-box">Descripation</div>
        <div className="description-box-nav-box fade">Review (122)</div>
      </div>
      <div className="descriptionbox-description">
        <p>
          An ecommerce platform is a software application that allows online
          businesses to manage their website, marketing, sales and operations.
        </p>
        <p>
          Online shopping for retail sales direct to consumers via web sites and
          mobile apps, conversational commerce via live chat, chatbots, and
          voice assistants.
        </p>
      </div>
    </div>
  );
};
export default Descripation;
