import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';
export default function Navbar(){

  const [menu, setMenu] = useState('menu');
  const {getTotalCartItems} = useContext(ShopContext);
  return(
    <>
  <div className='navbar'>
    <div className='nav-logo'>
      <img src={logo} alt="logo" />
      <p>Shopper</p>
    </div>
    <ul className='nav-menu'>
   
      <li onClick={()=>{setMenu('shop')}}><Link to='/myshop' style={{textDecoration:'none'}}>Shop</Link>{menu==='shop' ? <hr/>:<></>}</li> 
      <li onClick={()=>{setMenu('mens')}}><Link to='myshop/mens' style={{textDecoration:'none'}}>Men</Link>{menu==='mens' ? <hr/>:<></>}</li>
      <li onClick={()=>{setMenu('womens')}}><Link to='womens' style={{textDecoration:'none'}}>Women</Link>{menu==='womens' ? <hr/>:<></>}</li>
      <li onClick={()=>{setMenu('kids')}}><Link to='myshop/kids' style={{textDecoration:'none'}}>Kids</Link>{menu==='kids' ? <hr/>:<></>}</li>
    </ul>
    <div className='nav-login-cart'>
  
       <Link to='myshop/login'> <button>login</button></Link>
       <Link to='myshop/cart'><img src={cart_icon} alt='cart_icon' /></Link>
      <div className='nav-cart-count'>{getTotalCartItems()}</div>
    </div>
  </div>
    </>
  )
}
